def get_state_description(state: int = None, state_ex: int = None) -> str:
    """获取状态描述
    
    Args:
        state: 基本物流状态码
        state_ex: 详细物流状态码
        
    Returns:
        状态描述文本
    """
    # 如果有详细状态码，优先使用详细状态描述
    if state_ex is not None:
        return DETAILED_STATE_DESCRIPTIONS.get(state_ex, f"未知状态({state_ex})")
    
    # 否则使用基本状态描述
    if state is not None:
        return BASIC_STATE_DESCRIPTIONS.get(state, f"未知状态({state})")
    
    return "未知状态"


